open class Icon: Tag {
	open override class func createNode() -> Node {
		Node(type: .standard, name: "span")
	}

	init(_ icon: IconAsset, size: String) {
		super.init()

		self.setContents("<svg width='\(size)' height='\(size)' fill='currentColor' viewBox='0 0 256 256' version='1.1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' >\(icon.rawValue)</svg>")
	}
}
